{
    "id": "{{idpAdapterId}}",
    "name": "HTML Form PA QuickStart",
    "pluginDescriptorRef": {
        "id": "com.pingidentity.adapters.htmlform.idp.HtmlFormIdpAuthnAdapter"
    },
    "configuration": {
        "tables": [
            {
                "name": "Credential Validators",
                "rows": [
                    {
                        "fields": [
                            {
                                "name": "Password Credential Validator Instance",
                                "value": "{{pcvId}}"
                            }
                        ]
                    }
                ]
            }
        ],
        "fields": [
            {
                "name": "Challenge Retries",
                "value": "3"
            },
            {
                "name": "Session State",
                "value": "None"
            },
            {
                "name": "Session Timeout",
                "value": "60"
            },
            {
                "name": "Session Max Timeout",
                "value": "480"
            },
            {
                "name": "Allow Password Changes",
                "value": "false"
            },
            {
                "name": "Enable 'Remember My Username'",
                "value": "false"
            },
            {
                "name": "Show Password Expiring Warning",
                "value": "false"
            },
            {
                "name": "Password Reset Type",
                "value": "NONE"
            },
            {
                "name": "Account Unlock",
                "value": "false"
            },
            {
                "name": "Enable Username Recovery",
                "value": "false"
            },
            {
                "name": "Login Template",
                "value": "html.form.login.template.html"
            },
            {
                "name": "Logout Template",
                "value": "idp.logout.success.page.template.html"
            },
            {
                "name": "Change Password Template",
                "value": "html.form.change.password.template.html"
            },
            {
                "name": "Change Password Message Template",
                "value": "html.form.message.template.html"
            },
            {
                "name": "Password Management System Message Template",
                "value": "html.form.message.template.html"
            },
            {
                "name": "Change Password Email Template",
                "value": "message-template-end-user-password-change.html"
            },
            {
                "name": "Expiring Password Warning Template",
                "value": "html.form.password.expiring.notification.template.html"
            },
            {
                "name": "Threshold for Expiring Password Warning",
                "value": "7"
            },
            {
                "name": "Snooze Interval for Expiring Password Warning",
                "value": "24"
            },
            {
                "name": "Login Challenge Template",
                "value": "html.form.login.challenge.template.html"
            },
            {
                "name": "'Remember My Username' Lifetime",
                "value": "30"
            },
            {
                "name": "'This is My Device' Lifetime",
                "value": "30"
            },
            {
                "name": "Allow Username Edits During Chaining",
                "value": "false"
            },
            {
                "name": "Track Authentication Time",
                "value": "true"
            },
            {
                "name": "Post-Password Change Re-Authentication Delay",
                "value": "0"
            },
            {
                "name": "Password Reset Username Template",
                "value": "forgot-password.html"
            },
            {
                "name": "Password Reset Code Template",
                "value": "forgot-password-resume.html"
            },
            {
                "name": "Password Reset Template",
                "value": "forgot-password-change.html"
            },
            {
                "name": "Password Reset Error Template",
                "value": "forgot-password-error.html"
            },
            {
                "name": "Password Reset Success Template",
                "value": "forgot-password-success.html"
            },
            {
                "name": "Account Unlock Template",
                "value": "account-unlock.html"
            },
            {
                "name": "OTP Length",
                "value": "8"
            },
            {
                "name": "OTP Time to Live",
                "value": "10"
            },
            {
                "name": "Require Verified Email",
                "value": "false"
            },
            {
                "name": "Username Recovery Template",
                "value": "username.recovery.template.html"
            },
            {
                "name": "Username Recovery Info Template",
                "value": "username.recovery.info.template.html"
            },
            {
                "name": "Username Recovery Email Template",
                "value": "message-template-username-recovery.html"
            },
            {
                "name": "CAPTCHA for Authentication",
                "value": "false"
            },
            {
                "name": "CAPTCHA for Password change",
                "value": "false"
            },
            {
                "name": "CAPTCHA for Password Reset",
                "value": "false"
            },
            {
                "name": "CAPTCHA for Username recovery",
                "value": "false"
            }
        ]
    },
    "attributeContract": {
        "coreAttributes": [
            {
                "name": "policy.action",
                "masked": false,
                "pseudonym": false
            },
            {
                "name": "username",
                "masked": false,
                "pseudonym": true
            }
        ],
        "maskOgnlValues": false
    },
    "attributeMapping": {
        "attributeSources": [],
        "attributeContractFulfillment": {},
        "issuanceCriteria": {
            "conditionalCriteria": []
        }
    }
}

{
  "name": "edit",
  "description": "Used for PAQuickStart API App use case",
  "dynamic": "false"
}

{
    "id": "PAQuickStart",
    "name": "Internally Managed Reference Tokens",
    "pluginDescriptorRef": {
        "id": "org.sourceid.oauth20.token.plugin.impl.ReferenceBearerAccessTokenManagementPlugin"
    },
    "configuration": {
        "tables": [],
        "fields": [
            {
                "name": "Token Length",
                "value": "28"
            },
            {
                "name": "Token Lifetime",
                "value": "120"
            },
            {
                "name": "Lifetime Extension Policy",
                "value": "NONE"
            },
            {
                "name": "Maximum Token Lifetime",
                "value": ""
            },
            {
                "name": "Lifetime Extension Threshold Percentage",
                "value": "30"
            },
            {
                "name": "Mode for Synchronous RPC",
                "value": "3"
            },
            {
                "name": "RPC Timeout",
                "value": "500"
            }
        ]
    },
    "attributeContract": {
        "coreAttributes": [],
        "extendedAttributes": [
            {
                "name": "Username"
            },
            {
                "name": "OrgName"
            }
        ]
    },
    "selectionSettings": {
        "resourceUris": []
    },
    "accessControlSettings": {
        "restrictClients": false,
        "allowedClients": []
    },
    "sessionValidationSettings": {
        "checkValidAuthnSession": false,
        "checkSessionRevocationStatus": false,
        "updateAuthnSessionActivity": false
    }
}

{
    "redirectValidationLocalSettings": {
        "enableTargetResourceValidationForSSO": true,
        "enableTargetResourceValidationForSLO": true,
        "enableTargetResourceValidationForIdpDiscovery": true,
        "enableInErrorResourceValidation": true,
        "whiteList": [
            {
                "validDomain": "localhost",
                "validPath": "",
                "allowQueryAndFragment": false,
                "requireHttps": true,
                "targetResourceSSO": true,
                "idpDiscovery": false,
                "inErrorResource": false,
                "targetResourceSLO": true
            }
        ]
    },
    "redirectValidationPartnerSettings": {
        "enableWreplyValidationSLO": true
    }
}

{
    "name": "PAQuickStart Trusted Group",
    "useJavaTrustStore": true,
    "systemGroup": false,
    "ignoreAllCertificateErrors": false,
    "skipCertificateDateCheck": true,
    "certIds": [
        {{certId}}
    ]
}

{
    "enabled": true,
    "agentCacheInvalidatedResponseDuration": 900,
    "requireHTTPS": false,
    "name": "QuickStartAPI",
    "description": null,
    "manualOrderingEnabled": false,
    "resourceOrder": [],
    "applicationType": "API",
    "defaultAuthType": "API",
    "spaSupportEnabled": false,
    "destination": "Site",
    "contextRoot": "/PingAccessQuickStart/api",
    "realm": null,
    "caseSensitivePath": true,
    "webSessionId": 0,
    "siteId": {{siteId}},
    "agentId": 0,
    "virtualHostIds": [
        {{virtualHostId}}
    ],
    "identityMappingIds": {
        "Web": 0,
        "API": {{apiMapId}}
    },
    "accessValidatorId": 1,
    "policy": {
        "Web": [],
        "API": [
            {
                "type": "RuleSet",
                "id": {{apiRSId}}
            }
        ]
    }
}

{
    "name": "Root Resource",
    "methods": [
        "*"
    ],
    "pathPatterns": [
        {
            "pattern": "/*",
            "type": "WILDCARD"
        }
    ],
    "pathPrefixes": [
        "/*"
    ],
    "auditLevel": "ON",
    "anonymous": true,
    "enabled": true,
    "unprotected": false,
    "policy": {
        "Web": [],
        "API": []
    },
    "defaultAuthTypeOverride": null,
    "rootResource": true
}

{
    "name": "WAM Headers",
    "methods": [
        "*"
    ],
    "pathPatterns": [
        {
            "pattern": "/headers",
            "type": "WILDCARD"
        }
    ],
    "pathPrefixes": [
        "/headers"
    ],
    "auditLevel": "ON",
    "anonymous": false,
    "enabled": true,
    "unprotected": false,
    "policy": {
        "Web": [
            {
                "type": "Rule",
                "id": {{webRuleId}}
            }
        ],
        "API": []
    },
    "defaultAuthTypeOverride": null,
    "rootResource": false,
    "applicationId": {{webAppId}}
}

{
	"name": "API Headers",
    "methods": [
    	"*"
    ],
    "pathPatterns": [
    {
        "pattern": "/headers",
        "type": "WILDCARD"
    }
    ],
    "pathPrefixes": [
    	"/headers"
    ],
    "auditLevel": "ON",
    "rootResource": false,
    "anonymous": false,
    "enabled": true,
    "unprotected": false,
    "policy": {
    	"Web": [],
        "API": []
    },
    "defaultAuthTypeOverride": "Web",
    "applicationId": {{apiAppId}},
    "queryParamConfig": null,
    "resourceType": "Virtual",
    "resourceTypeConfiguration": {
    "responseGenerator": {
        "className": "com.pingidentity.pa.resources.responsegenerator.TemplateResponseGenerator",
        "configuration": {
        "template": "{\n  \"status\": \"200\",\n  \"message\": \"OK\"\n}",
            "responseCode": 200,
            "mediaType": "application/json;charset=utf-8"
        }
    }
    },
    "authenticationChallengePolicyId": null,
    "riskPolicyId": 0
}
